INSERT INTO method
(id, parent_id, user_id, name, description, type, tester) VALUES
(1, 1, 1, "Надёжность", "Фактор оценивает не только устойчивость продукта к различного вида сбоям, но и способности системы предотвращать пользовательские ошибки, давать возможность и средства для устранения этих ошибок", 'factor', 'both'),
(2, 2, 1, "Единообразие", "Использование одинаковых интерфейсных метафор, единых цветовых и шрифтовых схем в интерфейсе, непротиворечивость использованных решений предыдущему пользовательскому опыту, в том числе и при использовании других программных продуктов, интернет-ресурсов", 'factor', 'both'),
(3, 3, 1, "Очевидность состояния системы", "Индикация текущего состояния интерфейса и сообщение об ошибках, процессах обработки данных, изменении состояний, а так же отзывчивость и интерактивность системы, наличие реакций на действия пользователей", 'factor', 'both'),
(4, 4, 1, "Гибкость", "Возможность интерфейса адаптироваться к различным платформам, наличие локализаций для иностранных пользователей, альтернативные способы использования интерфейсов пользователями с ограниченными возможностями, соблюдение стандарта WCAG 2.0, поддержка разных типов пользователей от профессионалов до «новичков»", 'factor', 'both'),
(5, 5, 1, "Когнитивная простота", "Предоставление пользователям упрощенной модели понимания работы приложения, оценка понимания пользователями цепочки взаимодействий с интерфейсом, которые приведут к достижению цели", 'factor', 'both'),
(6, 6, 1, "Эргономическая простота", "Простота сенсорно-моторного взаимодействия с интерфейсом для пользователей (правило большого правого пальца для мобильных интерфейсов), соблюдение закона Фиттса", 'factor', 'both'),
(7, 7, 1, "Поисковая доступность", "Анализ распределения ключевых слов, иерархичность структуры приложения, организация поиска по контенту и функциональным элементам приложения или программы", 'factor', 'both'),
(8, 8, 1, "Качество контента", "Степень соответствия представленного текстового, аудио и визуального контента задачам пользователей, лаконичность и понятность представленной информации и наличие минимального набора данных для выработки управленческого решения", 'factor', 'both'),
(9, 9, 1, "Поддержка", "Наличие службы поддержки у программы или приложения, а так же других способов связи пользователей с командой разработчиков, проведение исследований степени удовлетворенности пользователей работой приложения, предоставление быстрой информации пользователям по работе с системой, подсказок и обучающих видео", 'factor', 'both'),
(10, 10, 1, "Функциональность", "Наличие устойчиво работающего стека функций, достаточных для решения пользователем конкретных задач, степень охвата функциональности не только непосредственных действий для решения проблемы, но и связанных операций", 'factor', 'both'),
(11, 11, 1, "Удовлетворённость", "Поддержание позитивного чувства у пользователей, связанного с работой в приложении и решением конкретных задач с его помощью", 'factor', 'both'),
(12, 12, 1, "Эмоциональность", "Использование определенных персонажей в интерфейсах, создание особого образа приложения, причисление интерфейсным элементам человеческих качеств (антропоморфизм), побуждение пользователей к восприятию приложения как живого существа, эмпатия", 'factor', 'both'),
(13, 13, 1, "Выгода от использования", "Формирование четких преимуществ от использования конкретного программного решения, доведения до сведения пользователей данных преимуществ и обеспечение их выполнения", 'factor', 'both'),
(14, 14, 1, "Вовлечённость", "Наличие мотивирующих факторов для повторного использования программного продукта или приложения, поддержание устойчивого положительного образа продукта среди пользователей, поддержка замороженных пользователей и предложение индивидуальных условий работы", 'factor', 'both'),
(15, 15, 1, "Язык приложения", "Использование специфической терминологии, свойственной целевой аудитории пользователей, тематики программы или приложения, без утери возможности использования ресурса начинающим пользователям, соблюдение грамматических и орфографических правил языка локализации приложения", 'factor', 'both'),
(16, 16, 1, "Общественное окружение", "Социальные каналы поддержки пользователей, наличие пользовательского сообщества вокруг приложения, формирование лояльных пользователей — «адвокатов» приложений", 'factor', 'both');
